	SET @stmt = CONCAT('UPDATE ', acc_tabl_name, ',',
						'(SELECT ', acc_tabl_name, '.code_of_account,
								ce.total_cost as cost,
								ce.updated as updated
						FROM ', acc_tabl_name, '
						JOIN (SELECT account,
