						WHERE updated_account.updated > 0
						and ', acc_tabl_name, '.code_of_account = updated_account.code_of_account;');
	PREPARE stmt FROM @stmt;
	EXECUTE stmt;
	DEALLOCATE PREPARE stmt;
END ;;
